import apiRequest from './api';
import * as mockApi from './mockApi';

// Flag to determine if we should use mock API
const USE_MOCK_API = true; // Set to false when backend is available

// User registration
export const register = async (userData) => {
  if (USE_MOCK_API) {
    return mockApi.register(userData);
  }
  
  return apiRequest('/auth/register/', {
    method: 'POST',
    body: JSON.stringify(userData)
  });
};

// User login
export const login = async (credentials) => {
  if (USE_MOCK_API) {
    const data = await mockApi.login(credentials);
    
    // Store token in localStorage
    if (data.token) {
      localStorage.setItem('token', data.token);
    }
    
    return data;
  }
  
  const data = await apiRequest('/auth/login/', {
    method: 'POST',
    body: JSON.stringify(credentials)
  });
  
  // Store token in localStorage
  if (data.token) {
    localStorage.setItem('token', data.token);
  }
  
  return data;
};

// User logout
export const logout = () => {
  if (USE_MOCK_API) {
    localStorage.removeItem('token');
    return Promise.resolve({ message: 'Logged out successfully' });
  }
  
  localStorage.removeItem('token');
};

// Get current user profile
export const getCurrentUser = async () => {
  if (USE_MOCK_API) {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('Not authenticated');
    }
    
    // Decode the token to get user data (for demo purposes)
    try {
      const userData = JSON.parse(atob(token));
      return userData;
    } catch (error) {
      throw new Error('Invalid token');
    }
  }
  
  return apiRequest('/auth/user/');
};

// Update user profile
export const updateProfile = async (userData) => {
  if (USE_MOCK_API) {
    // In a real mock implementation, we would update the user data
    // For now, we'll just return the user data as if it was updated
    return userData;
  }
  
  return apiRequest('/auth/user/', {
    method: 'PUT',
    body: JSON.stringify(userData)
  });
};

// Check if user is authenticated
export const isAuthenticated = () => {
  return !!localStorage.getItem('token');
};

// Get token
export const getToken = () => {
  return localStorage.getItem('token');
};
